 = 10 / 100 * sumOfTotal;	f.totalWhQty.value	= numFormatval(sumOfQty1+'',2);	f.totalQty.value	= addcomma(sumOfQty2);	f.total.value		= numFormatval(sumOfTotal.toString(), 2);	f.totalVat.value	= numFormatval(vat.toString(), 2);	f.totalAmount.value = numFormatval(sumOfTotal + vat + '',2);}</script>